sys_script_client.script:
function onSubmit() {
    //Type appropriate c   //Type appropriate comment here, and begin script below
    var err_message = " is not an invalid Email Address entered in Requestor's email. Format should be like: abc@army.mil";
    var email_regex = /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/;
    var phone_regex = /^[\+]?[(]?[0-9]{3}[)]?[-\s\.]?[0-9]{3}[-\s\.]?[0-9]{4,6}$/;

    var email = g_form.getValue('poc_email');
    var phone = g_form.getValue('poc_phone');
    var isValid = true;
    if (!email_regex.test(email)) {
        g_form.addErrorMessage(email + ' is an invalid Email Address');
        isValid = false;
    }
    if (!phone_regex.test(phone)) {
        g_form.addErrorMessage(phone + ' is  an invalid Phone number');
        isValid = false;
    }
    return isValid;

}
