sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var err_message = " is not an invalid Email Address entered in Requestor's email. Format should be like: abc@army.mil";
    var err_field = control.id;
    g_form.hideErrorBox(err_field);
    var regex = /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/;
    if (regex.test(newValue)) {
        return true;
    } else {
        g_form.showFieldMsg(err_field, newValue + err_message, 'error');
        return false;
    }
    //Type appropriate comment here, and begin script below

}
