sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    //https://gus.arl.army.mil/browse/AISP-1907
    //last visit date can't be earlier than first visit date
    var firstVisitDate = g_form.getValue('first_date_of_visit');
    if (newValue < firstVisitDate) {
        g_form.addErrorMessage('The Last Date of Visit cannot be before the First Date of Visit .');
        return false;
    }

}
