sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {
    var status = current.getValue('status');
    var msg = 'Visit Request to '+current.getValue('visit_name');
    switch (status) {
        case "awaiting_processing":
            msg = msg+': Received and Awaiting Processing';
            break;
        case "processed":
            msg = msg+': Request complete';
            break;
        case "pending_update":
            msg = msg+': Pending Additional Information from the Requestor';
            break;
        default:
            msg = msg+': Rejected';
    }
    email.setSubject(msg);



})(current, template, email, email_action, event);
