sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {
    var status = current.getValue('status');
    var msg = "";
    switch (status) {
        case "awaiting_processing":
            msg = 'Has been received and will be processed.';
            break;
        case "processed":
            msg = 'Has been submitted to ' + current.getValue('smo_code') + ' as requested.';
            break;
        case "pending_update":
            msg = 'The Security Office requests additional information:<br>' + current.getValue('mandatory_comment');
            break;
        default:
            msg = 'The Security Office rejected the Visit Request for the following reasons::<br>' + current.getValue('mandatory_comment');
    }
    template.print(msg);



})(current, template, email, email_action, event);
