sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {
    var attendees = current.getValue('attendees').split(",");
    var gr = new GlideRecord('sys_user');
    gr.addQuery("sys_id", "IN", attendees);
    gr.query();
    var i = 1;    
    if (gr.getRowCount()) {
        template.print("for the following attendees:<br>");

        while (gr.next()) {
            if (gr.email) {
                template.print("<br>Attendee " + i + ": " + gr.name + "(" + gr.email + ")");
            } else {
                template.print("<br>Attendee " + i + ": " + gr.name );
            }
            i++;
        }
    }




})(current, template, email, email_action, event);
