sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    var queryString = '';
    if (api.data.look_up_records_group_member.results.length = 1) {
        var groupName=api.data.look_up_records_group_member.results[0].group.displayValue.split(" ")[1];
        api.setState('groupName', groupName);
        switch (groupName) {
            case 'ALC':
                queryString = 'active=true^siteINalc,arl_central,arl_south';
                break;
            case 'APG':
                queryString = 'active=true^siteINapg,arl_northeast,arl_west';
                break;
            case 'ARO':
                queryString = 'active=true^siteINaro';
                break;
            default:
                queryString = 'active=true';
        }
        api.setState('queryString', queryString);
        api.setState('hideAllRequests', false);

    }else {
        api.setState('hideAllRequests', true);
    }


}
