sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {

    switch (api.context.props.groupName) {
        case 'ALC':
            return 'Service Area:ALC, ARL Central, ARL South';
        case 'APG':
            return 'Service Area:APG, ARL Northeast, ARL West';
        case 'ARO':
            return 'Service Area:ARO';
        default:
            return 'Service Area:All';
    }

}
