sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    helpers.timing.setTimeout(() => {
        api.emit("NOW_UXF_PAGE#SET_LOADING_STATE", {
            id: "page_load_blocker",
            loading: false
        });
    }, 2000);

    if (api.data.record.isValid) {
        var msg = 'Request is created. Please click on Back to List or close the tab '
        api.setState('disableSubmit', true);
        api.emit("NOW_UXF_PAGE#ADD_NOTIFICATIONS", {
            items: [{
                id: "alert1",
                status: "positive",
                icon: "circle-check-outline",
                content: {
                    type: "string",
                    value: msg
                },
                action: {
                    type: "dismiss"
                },
            }]
        });
    }


}
